mapper.update:
update post_test
set view_cnt = view_cnt + 1
where post_id = #{postId}

		UPDATE post_test
			SET title = #{title},
    			content = #{content}
		WHERE post_id = #{postId}

mapper.select:
SELECT post_Id, title, content , good, view_cnt, member_id 
FROM post_test
WHERE post_Id = #{postId}
