mapper.update:
update post
set view_cnt = view_cnt + 1
where post_id = #{postId}

		UPDATE post
			SET title = #{title},
    			content = #{content}
		WHERE post_id = #{postId}

mapper.select:
SELECT post_Id, title, content , good, view_cnt, member_id , category_id
FROM post
WHERE post_Id = #{postId}

SELECT category_id, category_name 
FROM category
WHERE board_id = #{boardId}

mapper.update:
    	UPDATE comments
		SET comment_contents = #{commentContents}
		WHERE comment_id = #{commentId}

UPDATE liketable
SET good = 0
WHERE member_id = #{memberId} AND post_id = #{postId}

UPDATE liketable
SET good = 1
WHERE member_id = #{memberId} AND post_id = #{postId}

mapper.select:
SELECT post_Id, title, content, good, view_cnt, member_id, category_id
FROM (
SELECT rownum rm, post_Id, title, content, good, view_cnt, member_id,
category_id
FROM post
WHERE member_id = #{memberId} 
) WHERE rm BETWEEN #{start} AND #{end}
ORDER BY post_Id DESC
